// Solution 1: Brute Force; three nested loops
// Time complexity: O(n3)
// Space complexity: O(1)

var threeSum1 = function(nums) {
    var [num1, num2, num3] = [0,0,0]
    var output = {}
    
    
    if (nums.length<3){
        return output
    }
    
    for (i=0; i<nums.length-2; i++){
        for (j=1+i; j<nums.length-1;  j++){
            for (k=2+i; k<nums.length; k++){
                num1 = nums[i]
                num2 = nums[j]
                num3 = nums[k]
                if (num1+num2+num3 == 0){
                    let triplet = [num1, num2, num3].sort((a, b) => a - b)
                    if(!output[triplet]){
                        output[triplet] = triplet
                    }
                }
            }
        }
        
    }
    output = Object.values(output)
    return output
    }
    
   
