// Solution 2: Incorporation twoSum function and dividing the input array into two arrays of positives and negatives. 

// Function to find two numbers whose sum is equal to the given target 
var twoSum = function(array, target){
    var output = []
    var seen = []

    array.forEach((item) => {
        if(seen.includes(target-item)){
            output.push([item, target-item, -target].sort((a,b) => a-b))
        }else {
            seen.push(item)
        }       
    });
    return output
}

var threeSum2 = function(nums){

// Divide given nums array into two arrays containing positives and negatives.

var [posArray, negArray, zerosArray] = [[],[],[]]
var [posArrayOutput, negArrayOutput] = [[],[]]
var combinedOutput = []
var combinedOutputObject = {}
var output = []

if (nums.length<3){
    return output
}


for (i=0; i<nums.length; i++){
    if(nums[i]> 0){
        posArray.push(nums[i])
    } else if(nums[i]< 0){
        negArray.push(nums[i])
    }else if(nums[i] == 0){
        zerosArray.push(nums[i])
    }
}


if (zerosArray.length>2){
    combinedOutput.push([0,0,0]) 
}

posArray = posArray.concat(zerosArray)

for (i=0; i<=negArray.length; i++){
        posArrayOutput = posArrayOutput.concat(twoSum(posArray, -negArray[i]))    
}

for (i=0; i<posArray.length; i++){
    negArrayOutput = negArrayOutput.concat(twoSum(negArray, -posArray[i]))
}

combinedOutput = combinedOutput.concat(posArrayOutput, negArrayOutput)

combinedOutput.forEach(element => {
    if(!combinedOutputObject[element]){
        combinedOutputObject[element] = element
    }
    return combinedOutputObject
})

output = Object.values(combinedOutputObject)
return output
}
