// Solution 3: Make solution 2 faster

// Function to find two numbers whose sum is equal to the given target 
var twoSum3 = function(array, target){
    var output = []
    var seen = []

    array.forEach((item) => {
        if(seen.includes(target-item)){
            output.push([item, target-item, -target].sort((a,b) => a-b))
        }else {
            seen.push(item)
        }       
    });
    return output
}

var threeSum3 = function(nums){

// Divide given nums array into two arrays containing positives and negatives.

var seen = []
var combinedOutput = []
var combinedOutputObject = {}
var output = []
var zerosArray = []

if (nums.length<3){
    return output
}

for (i=0; i<nums.length; i++){
    if(nums[i] == 0){
        zerosArray.push(nums[i])
    }
}


if (zerosArray.length>2){
    combinedOutput.push([0,0,0]) 
}


for (i=0; i<nums.length; i++){
        if (i>2){
        combinedOutput = combinedOutput.concat(twoSum3(seen, -nums[i]))
        }
        seen.push(nums[i]) 
}



combinedOutput.forEach(element => {
    if(!combinedOutputObject[element]){
        combinedOutputObject[element] = element
    }
    return combinedOutputObject
})

output = Object.values(combinedOutputObject)
return output
}

